import React, { Component } from 'react';

class Product extends Component {
    state = { 
        name : 'Berger',
        count : 6
     } 
    render() { 
        return (
        <div>
            <span className='btn btn-primary'>{this.state.name}</span>
            <span className='badge text-bg-secondary m'>{this.state.count}</span>
        </div>
        );
    }
}
 
export default Product;
